static void EventMenu(int pos)
{
	int bCommError = gCommStatus[gDeviceIndex];
	int flagBreak = FALSE;
	nCurrIndex = 1;
	EventMenuDisp(pos);

	(void)printf("\n\n\n EventMenu(nEventTotal=%d)\n\n\n\n", nEventTotal);
	if(nEventTotal != 0)
	{
//		bEventSend = TRUE;
		ReadyToSend();
		(void)EventSend(TRUE);
	}

	while (1)
	{
		E_KEY key = GetKey();

		if(key == TIME_OUT)
		{
			if(gOldFaultEventIndex[gDeviceIndex] != gFaultEventIndex[gDeviceIndex])
			{
				if(pos == 1)
				{
					nEventIndex = gFaultEventIndex[gDeviceIndex];
					nEventTotal = gFaultEventTotalCount[gDeviceIndex];
					EventMenuDisp(pos);
					gOldFaultEventIndex[gDeviceIndex] = gFaultEventIndex[gDeviceIndex];
					nCurrIndex = 1;
					bEventSend = TRUE;
					FaultCountWrite();
				}
			}
			(void)printf("TIME_OUT......\n");
			nSendStep = 0;
			statusSendStep = 0;
			gStatusSendEnd = STATUS_SEND_ING;
			(void)EventSend(FALSE);
		}
		else
		if(key == KEY_SETUP)
		{
			if(Setup() == SETUP_CHANGED)
			{
				flagBreak = TRUE;
			}
			EventMenuDisp(pos);
			(void)EventSend(TRUE);
		}
		else
		if(key == KEY_UP)
		{
			if(nEventTotal == 0)
			{
				continue;
			}
			if(nCurrIndex < nEventTotal)
			{
				nCurrIndex++;
			}
			else
			{
				nCurrIndex = 1;
			}
			ReadyToSend();
			(void)EventSend(TRUE);
			CountDisp(nEventTotal, nCurrIndex);
		}
		else
		if(key == KEY_DOWN)
		{
			if(nEventTotal == 0)
			{
				continue;
			}
			if(nCurrIndex > 1)
			{
				nCurrIndex--;
			}
			else
			{
				nCurrIndex = nEventTotal;
			}
			ReadyToSend();
			(void)EventSend(TRUE);
			CountDisp(nEventTotal, nCurrIndex);
		}
		else
		if(key == DATA_RECV)
		{
			if(bCommError == COMM_ERROR)
			{
				if(pos == 0)
				{
					nEventTotal = gSystemEventTotalCount[gDeviceIndex];
				}
				else
				{
					nEventTotal = gFaultEventTotalCount[gDeviceIndex];
				}
				nCurrIndex = 1;
				bCommError = COMM_OK;
				if(nEventTotal != 0)
				{
					gStatusSendEnd = STATUS_SEND_END;
					(void)EventSend(TRUE);
					continue;
				}
				else
				{
					EventMenuDisp(pos);
				}
			}
			if(gStatusSendEnd == STATUS_SEND_ING)
			{
				StatusRecv();
				if(gStatusSendEnd == STATUS_SEND_ING)
				{
					(void)EventSend(FALSE);
				}
				else
				{
					(void)printf("All done!!!\n");
					g_bRecvAllDone = TRUE;
					nSendStep = 0;
					statusSendStep = 0;
					gStatusSendEnd = STATUS_SEND_ING;
				}
			}
			else
			{
				(void)printf("All done!!!\n");
				EventDataRecv();
				g_bRecvAllDone = TRUE;
				nSendStep = 0;
				statusSendStep = 0;
				gStatusSendEnd = STATUS_SEND_ING;
			}
		}
		else
		if(key == KEY_CANCEL)
		{
			flagBreak = TRUE;
		}
		else
		if(key == KEY_COMM_ERROR)
		{
			(void)printf("COMM Error!!! gStatusSendEnd=%d, statusSendStep=%d, nSendStep=%d\n",gStatusSendEnd,statusSendStep,nSendStep);
			if(StatusRecvErrorProcess() == STATUS_SEND_ING)
			{
				nSendStep = 0;
			}
			else
			{
				g_bRecvAllDone = TRUE;
				(void)printf("All done!!!\n");
				gStatusSendEnd = STATUS_SEND_ING;
				statusSendStep = 0;
				nSendStep = 0;
			}
		}
		else
		if(key == COMM_STAT_ERROR)
		{
			bCommError = COMM_ERROR;
			nEventTotal = 0;
			EventMenuDisp(pos);
			gStatusSendEnd = STATUS_SEND_ING;
			statusSendStep = 0;
			nSendStep = 0;
		}
		else {}
		if(flagBreak == TRUE)
		{
			break;
		}
	}
}


